static int char2int(char a, int base)
{
	int i;
	if ((a>='A') && (a<='Z')) {
		i=a-'A'+10;
	} else if ((a>='a') && (a<='z')) {
		i=a-'a'+10;
	} else if ((a>='0') && (a<='9')) {
		i=a-'0';
	} else return -1;
	if (i>=base) return -1;
	return i;
}

static int parse_float(eval_scalar *f, char *fpn)
{
	char *end;
	double d;
	d=strtod(fpn, &end);
	if (*end==0) {
		f->type=SCALAR_FLOAT;
		f->scalar.floatnum.value=d;
		return 1;
	}
	return 0;		
}

int parse_integer(eval_scalar *i, char *num, int base, int lenmod)
{
	qword k=int_to_qword(0);
	int l=strlen(num)+lenmod;
	while (l--) {
		int c=char2int(*num, base);
		if (c==-1) return 0;
		k=qword_mul(k, int_to_qword(base));
		k=qword_add(k, int_to_qword(c));
		num++;
	}
	i->type=SCALAR_INT;
	i->scalar.integer.value=k;
	i->scalar.integer.type=TYPE_UNKNOWN;
	return 1;
}

static int parse_cstring(eval_scalar *r, char *s, int len)
{
	char *result;

	int alloclen=len;
	if (!len) alloclen=1;

	r->type=SCALAR_STR;
	r->scalar.str.value=(char*)malloc(alloclen);
/* translate */
	result=r->scalar.str.value;
	
	while (s && *s && len) {
		if (*s == '\\') {
			s++;len--;if (!len) break;
			switch (*s) {
				case '0':
					*result++='\0';
					break;
				case 'a':
					*result++='\a';
					break;
				case 'b':
					*result++='\b';
					break;
				case 'e':
					*result++='\e';
					break;
				case 'f':
					*result++='\f';
					break;
				case 'n':
					*result++='\n';
					break;
				case 'r':
					*result++='\r';
					break;
				case 't':
					*result++='\t';
					break;
				case 'v':
					*result++='\v';
					break;
				case '\"':
					*result++='"';
					break;
				case '\\':
					*result++='\\';
					break;
				case 'x': {
					int p, q;
					s++;len--;if (!len) break;
					p=char2int(*s, 16);
					if (p==-1)  return 0;
					s++;len--;if (!len) break;
					q=char2int(*s, 16);
					if (q==-1) return 0;
					*result++=(char)p*16+q;
					break;
				}
				default:
					*result++='\\';
					if (len) *result++=*s;
					break;
			}
		} else {
			*result++ = *s;
		}
		s++;len--;
	}
	
	r->scalar.str.len=result-r->scalar.str.value;
	
	return 1;
}

static int parse_pstring(eval_scalar *s, char *cstr, int len)
{
	int alloclen=len;
	if (!len) alloclen=1;
	
	s->type=SCALAR_STR;
	s->scalar.str.value=(char*)malloc(alloclen);
	memmove(s->scalar.str.value, cstr, len);
	s->scalar.str.len=len;
	return 1;
}

